import React, { useState } from 'react'
import './ControlComponent.css';

const ControlComponent = () => {
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [phone, setPhone] = useState();
    const [area, setArea] = useState();

    const handleSubmit = (e) => {
        e.preventDefault();
    }
    const getUsername = (event) => {
        setUsername(event.target.value);
    }
  return (
    <div className='box'>
      <form onSubmit={handleSubmit}>
        <div>
            <label>Username:</label>
            <input type='text' name='username' value={username} onChange={getUsername}/>
        </div>
        <div>
            <label>Password:</label>
            <input type='password' name='password' onChange={(event) => setPassword(event.target.value)} value={password}/>
        </div>
        <div>
            <label>Phone:</label>
            <input type='number' name='phone' onChange={(event) => setPhone(event.target.value)} value={phone}/>
        </div>
        <div>
            <label>Message:</label>
            <input type='n' name='phone' onChange={(event) => setPhone(event.target.value)} value={phone}/>
        </div>
        <button>Login</button>
      </form>
    </div>
  )
}

export default ControlComponent;
