import React, { useState } from 'react'
import './ControlComponent.css';

const ControlComponent = () => {
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [phone, setPhone] = useState();
    const [formData, setFormData] = useState(
        {
            username: '',
            password: '',
            phone: '',
            message: '',
            index: ''
        }
    )
    const handleSubmit = (e) => {
        e.preventDefault();
    }
    const getUsername = (event) => {
        setUsername(event.target.value);
    }
  return (
    <div className='box'>
      <form onSubmit={handleSubmit}>
        <div>
            <label>Username:</label>
            <input type='text' name='username' value={formData.username} onChange={getUsername}/>
        </div>
        <div>
            <label>Password:</label>
            <input type='password' name='password' onChange={(event) => setPassword(event.target.value)} value={formpassword}/>
        </div>
        <div>
            <label>Phone:</label>
            <input type='number' name='phone' onChange={(event) => setPhone(event.target.value)} value={phone}/>
        </div>
        <div>
            <label>Message:</label>
            <textarea id='' rows="3" name='message'/>
        </div>
        <button>Login</button>
      </form>
    </div>
  )
}

export default ControlComponent;
