import React, { useState } from "react";
import "./ControlComponent.css";
import ControlComponentDataTable from "../Data/ControlComponentDataTable/ControlComponentDataTable";

const ControlComponent = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    phone: "",
    message: "",
    index: "",
  });
  const handleSubmit = (e) => {
    if(formData.username === '' || formData.password === '' || formData.phone === '' || formData.message === '') {
        alert('Please fill in allfields')
    }
    let currentUserFormData = {
      username: formData.username,
      password: formData.password,
      phone: formData.phone,
      message: formData.message,
    };
    let oldUserData = [...userData, currentUserFormData];
    setUserData(oldUserData);
    setFormData({
        username: "",
        password: "",
        phone: "",
        message: "",
        index: "",
      })
    e.preventDefault();
  };

  let [userData, setUserData] = useState([]);
  const getValue = (e) => {
    let oldData = { ...formData };
    let inputName = e.target.name;
    let inputValue = e.target.value;
    oldData[inputName] = inputValue;
    setFormData(oldData);
  };

  return (
    <div className="box">
      <form onSubmit={handleSubmit}> 
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={getValue}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            onChange={getValue}
            value={formData.password}
          />
        </div>
        <div>
          <label>Phone:</label>
          <input
            type="number"
            name="phone"
            onChange={getValue}
            value={formData.phone}
          />
        </div>
        <div>
          <label>Message:</label>
          <textarea id="" rows="3" name="message" onChange={getValue} />
        </div>
        <button>{formData.index ? "Update" : "Save"}</button>
      </form>
      <ControlComponentDataTable userData={userData}/>
    </div>
  );
};

export default ControlComponent;
